import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';

import {
  Row,
  Col,
  ListGroup,
  Image,
  Form,
  Card,
  Button,
} from 'react-bootstrap';
import { addToCart } from '../actions/cartActions';

import Message from '../components/Message';

const CartScreen = ({ history, match, location }) => {
  const productId = match.params.id;
  // this is to get the query string mentioned in url //output will be ?qty=1
  const qty = location.search ? Number(location.search.split('=')[1]) : 1; //taking out the ?qty= and we want only after equals

  const dispatch = useDispatch();

  const cart = useSelector((state) => state.cart);
  const { cartDetails } = cart;
  console.log(cartDetails);
  useEffect(() => {
    if (productId) {
      dispatch(addToCart(productId, qty));
    }
  });

  return <div>Cart</div>;
};

export default CartScreen;
